import React from "react";
import Select from 'react-select';
import makeAnimated from 'react-select/animated';

import {
  Container,

} from "reactstrap";

import { FaSearch } from "react-icons/fa";
const animatedComponents = makeAnimated();


class Menucarrera extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      boolean : true
    }
   this.handleClick = this.handleClick.bind(this);

  }
  handleClick() {
    this.setState(state => ({
      boolean: !state.boolean
    }));
  }



  render() {
    return (
      <>
        <section className="section section-lg section-shaped">
        <style>{'body { background-color: orange; }'}</style>
        <Container>
        <div className="row">
          <div className="col-md-12">
            <div className="form-group">
              <div className="input-group input-group-alternative mb-4">

                <input className="form-control form-control-alternative" placeholder="ej: Libro de calculo I" type="text"></input>
                <div className="input-group-prepend">
                <button className="btn btn-icon btn-2 btn-white" type="button">
                <FaSearch /> 
              </button>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-6">

          </div>
          <div className="col-md-6">

        </div>


        </div>
        </Container>

        </section>
      </>
    );
  }
}

export default Menucarrera;
