import React from "react";
import ReactDOM from "react-dom";
import MUIDataTable from "mui-datatables";
// nodejs library that concatenates classes
import classnames from "classnames";
// reactstrap components
import {
  Card,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  Row,
  Col
} from "reactstrap";

import * as Constants from './misconstantes'


class TabsSection extends React.Component {

  state = {
    icontTabs2: [],
    nav_item: [],
  };

  toggleNavs = (e, state, index) => {
    e.preventDefault();
    this.setState({
      [state]: index
    });

    if(this.state.icontTabs2.includes(index)){
      var z = this.state.icontTabs2.indexOf(index);
      this.state.icontTabs2.splice(z,1);
    }
    else{
      this.state.icontTabs2.push(index)
    }

  };


  render() {
    this.state.nav_item =[]
    for (let i = 0; i < categorias.length; i++) {   
      this.state.nav_item.push(      
        <NavItem key={i} >
          <NavLink key={i} 
            aria-selected="true"
            className={classnames("mb-sm-3 mb-md-1 ", {
              active: this.state.icontTabs2.includes(i),            
            })}

            onClick={e => this.toggleNavs(e, "iconTabs", i)}
            href="#"
            role="tab"       
          >
            <i className={categorias[i].icon} />
            {categorias[i].label}
          </NavLink>
        </NavItem>
        )
    }

    return (
      <>
        <Row className="justify-content-center">
          <Col lg="12">
            <div className="nav-wrapper">
              <Nav className="nav-fill flex-column flex-md-row" id="tabs-icons-text" pills role="tablist" >
                 {this.state.nav_item}         
              </Nav>
            </div>
            <MUIDataTable
              data={data}
              columns={columns}
              options={options}
            />

          </Col>
        </Row>


      </>
    );
  }
}

export default TabsSection;
