import React from "react";
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
import Search from "./IndexSections/Search.jsx";
import Tabs from "./IndexSections/Tabs";
import {
  Container,

} from "reactstrap";


const animatedComponents = makeAnimated();

const colourStyles = {
  multiValue: (styles,) => {
    return {
      ...styles,
      backgroundColor: "white"
    };
  },
  multiValueLabel: (styles) => ({
    ...styles,
    color: "#172b4d"
  }),
  multiValueRemove: (styles) => ({
    ...styles,
    color: "#172b4d",
    ":hover": {
      backgroundColor: "#172b4d",
      color: "#fff"
    }
  })
};

const carreras=[
  { value: '1', nombre: 'Informatica',ramos:[
    {value:'1.1', nombre:'ramo1',otro:'otro1'},
    {value:'1.2', nombre:'ramo2',otro:'otro2'},
    {value:'1.3', nombre:'ramo3',otro:'otro3'},

] },
{ value: '2', nombre: 'Medicina',ramos:[
  {value:'2.1', nombre:'ramo1',otro:'otro1'},
  {value:'2.2', nombre:'ramo2',otro:'otro2'},
  {value:'2.3', nombre:'ramo3',otro:'otro3'},

] },
{ value: '3', nombre: 'Contruccion',ramos:[
  {value:'3.1', nombre:'ramo1',otro:'otro1'},
  {value:'3.2', nombre:'ramo2',otro:'otro2'},
  {value:'3.3', nombre:'ramo3',otro:'otro3'},

] },
]
const ramos=[
  { value: '1', label: 'Calculo 1' },
  { value: '2', label: 'Algebra 2' },
  { value: '3', label: 'Intro a la comp.' }
]


class Inicio extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      boolean : true
    }
   this.handleClick = this.handleClick.bind(this);

  }
  handleClick() {
    this.setState(state => ({
      boolean: !state.boolean
    }));
  }



  render() {
    return (
      <>
        <section>
          <Container>
            <div className="row">
              <div className="col-md-12">
                <div className="form-group">
                  <Search></Search>
                </div>
              </div>
              <div className="col-md-6">
                <Select
                closeMenuOnSelect={true}
                components={animatedComponents}
                placeholder="Todas las carreras"
                isMulti
                options={carreras}
                styles={colourStyles}

                />
              </div>
              <div className="col-md-6">
                <Select
                closeMenuOnSelect={true}
                components={animatedComponents}
                placeholder="Todos los ramos"
                isMulti

                styles={colourStyles}

                />
              </div>
            </div>
            <Tabs></Tabs>
          </Container>
        </section>
      </>
    );
  }
}

export default Inicio;
