    {value: '1.2', nombre: 'libro calculo 2', link:'libro2.pdf', formato:'pdf' }, 

  ]},
  { value: '2', label: 'Guias',icon: 'fa fa-chain-broken mr-2', material: [

    {value: '2.1', nombre: 'Guia calculo 1', link:'Guia.pdf', formato:'pdf' },
    {value: '2.2', nombre: 'Guia calculo 2', link:'Guia.pdf', formato:'pdf' } 

  ]},
  { value: '3', label: 'Pruebas', icon: 'fa fa-file-pdf-o mr-2',material: [

    {value: '3.1', nombre: 'Prueba calculo 1', link:'Prueba.pdf', formato:'pdf' },
    {value: '3.2', nombre: 'Prueba calculo 2', link:'Prueba.pdf', formato:'pdf' } 

  ]},
  { value: '4', label: 'Videos', icon: 'fa fa-youtube-play mr-2',material: [

    {value: '4.1', nombre: 'Video para la prueba de  calculo 1', link:'Plinkrueba.pdf', formato:'pdf' },
    {value: '4.2', nombre: 'Video para la prueba de calculo 2', link:'link.pdf', formato:'pdf' } 

  ]},
  { value: '5', label: 'Pautas', icon: 'fa fa-file-word-o mr-2',material: [

    {value: '5.1', nombre: 'Video para la prueba de  calculo 1', link:'Plinkrueba.pdf', formato:'pdf' },
    {value: '5.2', nombre: 'Video para la prueba de calculo 2', link:'link.pdf', formato:'pdf' } 

  ]},
  { value: '6', label: 'Trabajos', icon: 'fa fa-file-archive-o mr-2',material: [

    {value: '6.1', nombre: 'Video para la prueba de  calculo 1', link:'Plinkrueba.pdf', formato:'pdf' },
    {value: '6.2', nombre: 'Video para la prueba de calculo 2', link:'link.pdf', formato:'pdf' } 

  ]},
]

const columns = [
  {
    name: "Nombre",
    options: {
      filter: true
    }
  },
  {
    name: "edad",
    options: {
      filter: false
    }
  },
  {
    name: "Valoracion",
    options: {
      filter: false
    }
  },
  {
    name: "Categoria",
    options: {
      filter: false,
      filterList: [], //para filtrar por categoria
      display: "true"
    }
  },

  "Enlace"
];

const data = [
  ["Gabby George", "Business Analyst", "Minneapolis", 30, "$100,000"],
  ["Aiden Lloyd", "Business Consultant", "Dallas", 55, "$200,000"],
  ["Jaden Collins", "Attorney", "Santa Ana", 27, "$500,000"],
  ["Franky Rees", "Business Analyst", "St. Petersburg", 22, "$50,000"],
  ["Aaren Rose", "Business Consultant", "Toledo", 28, "$75,000"],
  [
    "Blake Duncan",
    "Business Management Analyst",
    "San Diego",
    65,
    "$94,000"
  ],
  ["Frankie Parry", "Agency Legal Counsel", "Jacksonville", 71, "$210,000"],
  ["Lane Wilson", "Commercial Specialist", "Omaha", 19, "$65,000"],
  ["Robin Duncan", "Business Analyst", "Los Angeles", 20, "$77,000"],
  ["Mel Brooks", "Business Consultant", "Oklahoma City", 37, "$135,000"],
  ["Harper White", "Attorney", "Pittsburgh", 52, "$420,000"],
  ["Kris Humphrey", "Agency Legal Counsel", "Laredo", 30, "$150,000"],
  ["Frankie Long", "Industrial Analyst", "Austin", 31, "$170,000"],
  ["Brynn Robbins", "Business Analyst", "Norfolk", 22, "$90,000"],
  ["Justice Mann", "Business Consultant", "Chicago", 24, "$133,000"],
  [
    "Addison Navarro",
    "Business Management Analyst",
    "New York",
    50,
    "$295,000"
  ],
  ["Jesse Welch", "Agency Legal Counsel", "Seattle", 28, "$200,000"],
  ["Eli Mejia", "Commercial Specialist", "Long Beach", 65, "$400,000"],
  ["Gene Leblanc", "Industrial Analyst", "Hartford", 34, "$110,000"],
  ["Danny Leon", "Computer Scientist", "Newark", 60, "$220,000"],
  ["Lane Lee", "Corporate Counselor", "Cincinnati", 52, "$180,000"],
  ["Jesse Hall", "Business Analyst", "Baltimore", 44, "$99,000"],
  ["Danni Hudson", "Agency Legal Counsel", "Tampa", 37, "$90,000"],
  ["Terry Macdonald", "Commercial Specialist", "Miami", 39, "$140,000"],
  ["Justice Mccarthy", "Attorney", "Tucson", 26, "$330,000"],
  ["Silver Carey", "Computer Scientist", "Memphis", 47, "$250,000"],
  ["Franky Miles", "Industrial Analyst", "Buffalo", 49, "$190,000"],
  ["Glen Nixon", "Corporate Counselor", "Arlington", 44, "$80,000"],
  [
    "Gabby Strickland",
    "Business Process Consultant",
    "Scottsdale",
    26,
    "$45,000"
  ],
  ["Mason Ray", "Computer Scientist", "San Francisco", 39, "$142,000"]
];

const options = {
  filterType: "dropdown",
  responsive: "scroll",
  download: false,
  print: false,
  viewColumns: false,
  selectableRows: false,
  filter: false,
  sortFilterList: false
};



class TabsSection extends React.Component {

  state = {
    icontTabs2: [],
    nav_item: [],
  };

  toggleNavs = (e, state, index) => {
    e.preventDefault();
    this.setState({
      [state]: index
    });

    if(this.state.icontTabs2.includes(index)){
      var z = this.state.icontTabs2.indexOf(index);
      this.state.icontTabs2.splice(z,1);
    }
    else{
      this.state.icontTabs2.push(index)
    }

  };


  render() {
    this.state.nav_item =[]
    for (let i = 0; i < categorias.length; i++) {   
      this.state.nav_item.push(      
        <NavItem key={i} >
          <NavLink key={i} 
            aria-selected="true"
            className={classnames("mb-sm-3 mb-md-1 ", {
              active: this.state.icontTabs2.includes(i),            
            })}

            onClick={e => this.toggleNavs(e, "iconTabs", i)}
            href="#"
            role="tab"       
          >
            <i className={categorias[i].icon} />
            {categorias[i].label}
          </NavLink>
        </NavItem>
        )
    }

    return (
      <>
        <Row className="justify-content-center">
          <Col lg="12">
            <div className="nav-wrapper">
              <Nav className="nav-fill flex-column flex-md-row" id="tabs-icons-text" pills role="tablist" >
                 {this.state.nav_item}         
              </Nav>
            </div>
            <MUIDataTable

              data={data}
              columns={columns}
              options={options}
            />

          </Col>
        </Row>


      </>
    );
  }
}

export default TabsSection;
