import React from "react";
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
import Search from "./IndexSections/Search.jsx";
import Tabs from "./IndexSections/Tabs";
import {
  Container,

} from "reactstrap";

import { FaSearch } from "react-icons/fa";
const animatedComponents = makeAnimated();

const carreras=[
  { value: '1', label: 'Informatica' },
  { value: '2', label: 'Construccion' },
  { value: '3', label: 'Enfermeria' }
]
const ramos=[
  { value: '1', label: 'Calculo 1' },
  { value: '2', label: 'Algebra 2' },
  { value: '3', label: 'Intro a la comp.' }
]

const colourStyles = {
  control: styles => ({ ...styles, backgroundColor: 'white' }),
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    const color = chroma(data.color);
    return {
      ...styles,
      backgroundColor: isDisabled ? 'red' : blue,
      color: '#FFF',
      cursor: isDisabled ? 'not-allowed' : 'default',

    };
  },

};

class Inicio extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      boolean : true
    }
   this.handleClick = this.handleClick.bind(this);

  }
  handleClick() {
    this.setState(state => ({
      boolean: !state.boolean
    }));
  }



  render() {
    return (
      <>
        <section className="section section section-shaped ">
          <div className="shape shape-style-1 bg-gradient-purple ">
              <span />
              <span />
              <span />
              <span />
              <span />
              <span />
              <span />
              <span />
            </div>
            <br></br><br></br>  
          <Container>
            <div className="row">
              <div className="col-md-12">
                <div className="form-group">
                  <Search></Search>
                </div>
              </div>
              <div className="col-md-6">
                <Select
                closeMenuOnSelect={true}
                components={animatedComponents}
                placeholder="Todas las carreras"
                isMulti
                options={carreras}
                styles={colourStyles}
                />
              </div>
              <div className="col-md-6">
                <Select
                closeMenuOnSelect={true}
                components={animatedComponents}
                placeholder="Todos los ramos"
                isMulti
                options={ramos}

                />
              </div>
            </div>
            <Tabs></Tabs>
          </Container>

        </section>
      </>
    );
  }
}

export default Inicio;
