import React from "react";
// nodejs library that concatenates classes
import classnames from "classnames";

// reactstrap components
import {
  Card,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Row,
  Col
} from "reactstrap";

const categorias=[
  { value: '1', label: 'Libros',icon: 'fa fa-book mr-2', material: [

    {value: '1', nombre: 'libro calculo 1', link:'libro1.pdf', formato:'pdf' },
    {value: '2', nombre: 'libro calculo 2', link:'libro2.pdf', formato:'pdf' } 

  ]},
  { value: '2', label: 'Guias',icon: 'fa fa-chain-broken mr-2', material: [

    {value: '1', nombre: 'Guia calculo 1', link:'Guia.pdf', formato:'pdf' },
    {value: '2', nombre: 'Guia calculo 2', link:'Guia.pdf', formato:'pdf' } 

  ]},
  { value: '3', label: 'Pruebas', icon: 'fa fa-file-text mr-2',material: [

    {value: '1', nombre: 'Prueba calculo 1', link:'Prueba.pdf', formato:'pdf' },
    {value: '2', nombre: 'Prueba calculo 2', link:'Prueba.pdf', formato:'pdf' } 

  ]},
  { value: '4', label: 'Videos', icon: 'fa fa-file-text mr-2',material: [

    {value: '1', nombre: 'Video para la prueba de  calculo 1', link:'Prueba.pdf', formato:'pdf' },
    {value: '2', nombre: 'Prueba calculo 2', link:'Prueba.pdf', formato:'pdf' } 

  ]},
]



class TabsSection extends React.Component {

  state = {
    iconTabs: 0,
    plainTabs: 1,
    nav_item: [],
    nav_cont: []
  };

  toggleNavs = (e, state, index) => {
    e.preventDefault();
    this.setState({
      [state]: index
    });
  };


  render() {

    this.state.nav_cont2 =[]
    this.state.nav_item =[]
    this.state.nav_cont =[]
    for (let i = 0; i < categorias.length; i++) {

      this.state.nav_item.push(      
        <NavItem key={i} >
          <NavLink key={i} 
            aria-selected={this.state.iconTabs === i}
            className={classnames("mb-sm-3 mb-md-1", {
              active: this.state.iconTabs === i
            })}
            onClick={e => this.toggleNavs(e, "iconTabs", i)}
            href="#"
            role="tab"

          >
            <i className={categorias[i].icon} />

            {categorias[i].label}
          </NavLink>
        </NavItem>
        )
        for (let j = 0; j < categorias[i].material.length; j++) {        
          this.state.nav_cont.push(      
            <TabContent  key={i} activeTab={"iconTabs" + this.state.iconTabs}>
              <TabPane key={i}  tabId={"iconTabs"+i}>

                  <div className="row" align="center">
                    <div className="col-sm">
                      <strong>{categorias[i].material[j].nombre}</strong>
                    </div>

                    <div className="col-sm">
                      <i class="fa fa-star" aria-hidden="true"></i>
                      <i class="fa fa-star" aria-hidden="true"></i>
                      <i class="fa fa-star" aria-hidden="true"></i>
                      <i class="fa fa-star-o" aria-hidden="true"></i>
                      <i class="fa fa-star-o" aria-hidden="true"></i>
                    </div>

                    <div className="col-sm">
                      <a href={categorias[i].material[j].link}><i class="fa fa-arrow-circle-o-down fa-2x" ></i></a>
                    </div>
                  </div>           
            </TabPane>
          </TabContent>
          )
    }
    }

    return (
      <>
        <Row className="justify-content-center">
          <Col lg="12">
            <div className="nav-wrapper">
              <Nav className="nav-fill flex-column flex-md-row" id="tabs-icons-text" pills role="tablist" >
                 {this.state.nav_item}         
              </Nav>
            </div>
            <Card className="shadow">
            <CardBody >
               {this.state.nav_cont}
            </CardBody>
            </Card>
          </Col>
        </Row>

      </>
    );
  }
}

export default TabsSection;
