import React from "react";
import Select from 'react-select';
import makeAnimated from 'react-select/animated';

import {
  Container,
  Row,
  Col
} from "reactstrap";

import { FaSearch } from "react-icons/fa";
const animatedComponents = makeAnimated();

const carreras=[
  { value: '1', label: 'Informatica' },
  { value: '2', label: 'Construccion' },
  { value: '3', label: 'Enfermeria' }
]
const ramos=[
  { value: '1', label: 'Calculo 1' },
  { value: '2', label: 'Algebra 2' },
  { value: '3', label: 'Intro a la comp.' }
]
const años=[
  { value: '1', label: '2019' },
  { value: '2', label: '2018' },
]

const customStyles = {
  option: (provided, state) => ({
    ...provided,
    borderBottom: '1px dotted pink',
    color: state.isSelected ? 'red' : 'blue',
    padding: 20,
  }),
  control: () => ({
    // none of react-select's styles are passed to <Control />
    width: 200,
  }),
  singleValue: (provided, state) => {
    const opacity = state.isDisabled ? 0.5 : 1;
    const transition = 'opacity 300ms';

    return { ...provided, opacity, transition };
  }
}

class Inicio extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      boolean : true
    }
   this.handleClick = this.handleClick.bind(this);

  }
  handleClick() {
    this.setState(state => ({
      boolean: !state.boolean
    }));
  }



  render() {
    return (
      <>
        <section className="section section-lg section-shaped">
        <style>{'body { background-color: orange; }'}</style>
        <Container>
        <div className="row">
          <div className="col-md-12">
            <div className="form-group">
              <div className="input-group input-group-alternative mb-4">

                <input className="form-control form-control-alternative" placeholder="ej: Libro de calculo I" type="text"></input>
                <div className="input-group-prepend">
                <button className="btn btn-icon btn-2 btn-white" type="button">
                <FaSearch /> 
              </button>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-6">
            <Select
            closeMenuOnSelect={true}
            components={animatedComponents}
            placeholder="Todas las carreras"
            isMulti
            options={carreras}
            />
          </div>
          <div className="col-md-6">
            <Select

            closeMenuOnSelect={true}
            components={animatedComponents}
            placeholder="Todos los ramos"
            isMulti
            options={ramos}
            styles={customStyles}
            />
          </div>


        </div>
        </Container>

        </section>
      </>
    );
  }
}

export default Inicio;
