import React from "react";
import axios from 'axios';
import { IoIosThumbsUp } from "react-icons/io";
// reactstrap components
import {
  Button,
  Card,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  UncontrolledAlert,
  CardHeader
} from "reactstrap";


class Register extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      nombre: '',
      rut: '',
      email:'',
      password:'',
      c_password:'',
      alert: false,
      mensaje:''
    };
    this.handleSubmit = this.handleSubmit.bind(this); 
    this.onDismiss = this.onDismiss.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  onDismiss() {
    this.setState({alert: false})
  }

  handleInputChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value

    this.setState({
      [name]: value
    });
  }




  handleSubmit(event) {
    event.preventDefault();
    //console.log(this.state)

    axios.post('http://localhost:8000/register', {
      nombre: this.state.nombre,
      rut: this.state.rut,
      email: this.state.email,
      password: this.state.password,
      c_password: this.state.password
    })
    .then(response => { 
      this.props.history.push('/login-page')
    })
    .catch(error => {
      //console.log(error.response.data.message)
      this.setState({alert: true})
      this.setState({mensaje: error.response.data.message})

    });

  }


  render() {
    const {handleClick} = this.props;

    return (
      <>
       <section>
       <div className="transform-perspective-right">
            <Card className="bg-secondary shadow border-0">
                <CardHeader className="bg-white pb-4">
                     <div className="text text-center">
                        <strong><big>Registro </big></strong>
                    </div>
                </CardHeader>
                <CardBody className=" px-lg-5 py-lg-5">
                    <UncontrolledAlert color="danger" fade={false} isOpen={this.state.alert} toggle={this.onDismiss}>
                        <span className="alert-inner--icon">


                        </span>{" "}
                        <span className="alert-inner--text">
                            <strong>Error!</strong> {this.state.mensaje}
                        </span>
                    </UncontrolledAlert>
                    <Form onSubmit={this.handleSubmit}>
                        <FormGroup>
                            <InputGroup className="input-group-alternative mb-3">
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>
                                <i className="ni ni-hat-3" />
                                </InputGroupText>
                            </InputGroupAddon>
                            <Input placeholder="nombre" name="nombre" type="text" onChange={this.handleInputChange}/>
                            </InputGroup>
                        </FormGroup>

                        <FormGroup>
                            <InputGroup className="input-group-alternative mb-3">
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>
                                <i className="ni ni-hat-3" />
                                </InputGroupText>
                            </InputGroupAddon>
                            <Input  name="rut" type="text" pattern="[0-9]{7,8}-[0-9Kk]{1}"  placeholder="12345678-5" onChange={this.handleInputChange}/>
                            </InputGroup>
                        </FormGroup>

                        <FormGroup>
                            <InputGroup className="input-group-alternative mb-3">
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>
                                <i className="ni ni-email-83" />
                                </InputGroupText>
                            </InputGroupAddon>
                            <Input placeholder="Email" type="email" name="email" onChange={this.handleInputChange}/>
                            </InputGroup>
                        </FormGroup>
                        <FormGroup>
                            <InputGroup className="input-group-alternative">
                            <InputGroupAddon addonType="prepend">
                                <InputGroupText>
                                <i className="ni ni-lock-circle-open" />
                                </InputGroupText>
                            </InputGroupAddon>
                            <Input
                                placeholder="Password"
                                type="password"
                                autoComplete="off"
                                name="password"
                                onChange={this.handleInputChange}
                            />
                            </InputGroup>
                        </FormGroup>

                        <div className="text-center"> 
                            <Button
                            className="mt-4"
                            color="primary"
                            type="submit"
                            >
                            Crear Cuenta
                            </Button>
                        </div>
                        <div className="text-center">
                        <hr></hr>
                        <a className="text-light text-center" onClick={handleClick} href="/#">
                            <small>Ya tengo una cuenta.  <strong>Iniciar Sesión</strong></small>
                        </a>
                        </div>
                    </Form>
                </CardBody>
            </Card>  
          </div>
        </section>

      </>
    );
  }
}

export default Register;
