import React from "react";
import MUIDataTable from "mui-datatables";
import { createMuiTheme, MuiThemeProvider } from '@material-ui/core/styles';
// nodejs library that concatenates classes
import classnames from "classnames";
// reactstrap components
import {
  NavItem,
  NavLink,
  Nav,
  Row,
  Col
} from "reactstrap";
import * as Constants from './misconstantes'


class TabsSection extends React.Component {

  state = {
    icontTabs2: [],
    nav_item: [],
  };

  toggleNavs = (e, state, index) => {
    e.preventDefault();
    this.setState({
      [state]: index
    });
    if(this.state.icontTabs2.includes(index)){
      var z = this.state.icontTabs2.indexOf(index);
      this.state.icontTabs2.splice(z,1);
    }
    else{
      this.state.icontTabs2.push(index)
    }

  };


  render() {
    this.state.nav_item =[]
    for (let i = 0; i < Constants.categorias.length; i++) {   
      this.state.nav_item.push(      
        <NavItem key={i} >
          <NavLink key={i} 
            aria-selected="true"
            className={classnames("mb-sm-3 mb-md-1 ", {
              active: this.state.icontTabs2.includes(i),            
            })}

            onClick={e => this.toggleNavs(e, "iconTabs", i)}
            href="#"
            role="tab"       
          >
            <i className={Constants.categorias[i].icon} />
            {Constants.categorias[i].label}
          </NavLink>
        </NavItem>
        )
    }
    const columns = [
      {
        name: "Nombre",
        options: {
          filter: true
        }
      },
      {
        name: "Ramo",
        options: {
          filter: true
        }
      },
      {
        name: "Usuario",
        options: {
          filter: true
        }
      },
      {
        name: "Formato",
        options: {
          filter: false
        }
      },
      {
        name: "Valoracion",
        options: {
          filter: false
        }
      },
      {
        name: "categoria",
        options: {
          filter: false,
          filterList: [], //para filtrar por categoria
          display: "false"
        }
      },
      {
        name: "aÃ±o",
        options: {
          filter: false,
        }
      },
      {
        name: "Enlace",
        options: {
          filter: false,
          customBodyRender: (value, tableMeta, updateValue) => (
            <FormControlLabel
              value={value}
              control={<TextField value={value} />}
              onChange={event => updateValue(event.target.value)}
            />
          )

        },
      },
    ];


    return (
      <>
        <Row className="justify-content-center">
          <Col lg="12">
            <div className="nav-wrapper">
              <Nav className="nav-fill flex-column flex-md-row" id="tabs-icons-text" pills role="tablist" >
                 {this.state.nav_item}         
              </Nav>
            </div>

         <div  style= {{  position: "sticky"}}> 

            <MUIDataTable 
              data={Constants.data}
              columns={columns}
              options={Constants.options}
            />  
             </div>

          </Col>
        </Row>


      </>
    );
  }
}

export default TabsSection;
